	 return pet_id,msg_channel,306
	end 	
	if type(player_level)~="number" or player_level<=0 or player_level>150 then
	 ---//print("i am here",player_level)
	 return pet_id,msg_channel,307
	end 	

	
	withskillid,msg_id=ZCatchSkillIDFromRace(monster_raceinfo)
	skill_alvl,skill_blvl=PlayerQuerySkilllvl(withskillid)
	
	totalchance=10*skill_alvl*ZCatchChanceFromDifficulty(monster_catchdifficulty)*ZCatchChanceFromHP(monster_hp)*ZCatchChanceFromLevels(monster_level,player_level)
	iscatch=ZMiscThrowDice(totalchance)
	if iscatch==1 then 
		pet_id=ZPetCatchResult(monster_id)
		---msg_id=308
		msg_id=310+msg_id	
	else
		pet_id=0
		msg_id=309
	end 
		return pet_id,msg_channel,msg_id
	
end

