---//获得配方的基本属性信息--------------------------------------------------------------------------
function ZGetRecipeProp(recipe_id)
	if type(recipe_id)~="number" then
	 return 0,0,401
	end 	
	local recipe_group=99				---//0,人物生产，1,宠物生产 ,99-error
	local recipe_level=99				---//配方等级1-10,99-error
	local recipe_type=99					---//1,人物武器，2人物衣服，3人物帽子，4人物鞋子，5，人物其它
															---//11 宠物相关,99-error
	local recipelist=ZStaticRecipeList()
	for i,v in pairs(recipelist) do
		if v.recipe_id == recipe_id then 
			recipe_group=v.recipe_group 
			recipe_level=v.recipe_level
			recipe_type=v.recipe_type
		end
	end
	
	if recipe_gourp~=1 or recipe_gourp~=0 then recipe_gourp=99 end   	
	if recipe_level<1 or recipe_level>10 then recipe_level=99 end  
	if recipe_type~=1 or recipe_type~=2 or recipe_type~=3 or recipe_type~=4 or recipe_type~=5 or recipe_type~=11 then recipe_type=99 end 												
	return recipe_group,recipe_level,recipe_type
end 
---//获得配方对应的技能等级信息--------------------------------------------------------------------------
function ZGetRecipeWithSkilllevel(recipe_type)
	if recipe_type==99 or type(recipe_type)~="number" then return 0 end 
	return 1
end
---//获得配方对应的产出信息-------------------------------------------------------------------------
function ZGetRecipeGainItems(recipe_group,recipe_level,recipe_type)
	
	return 685,2
end

---//生产获得额外产出主函数--------------------------------------------------------------------------
function ExtGainOfCreat(recipe_id, player_level)
	local item_id=0										---//产出物品id
	local item_count=0								---//产出物品数量
	local msg_id=401									---//提示信息
	local psvskill_level=0						---//对应技能等级
	local chance=0										---//产出物品概率
		local recipe_group,recipe_level,recipe_type=0,1,1
		
	if type(recipe_id)~="number" or type(player_level)~="number" or player_level<0 or player_level>150 then
	 return item_id,item_count,401
	end 	

	recipe_group,recipe_level,recipe_type=ZGetRecipeProp(recipe_id)
	psvskill_level=ZGetRecipeWithSkilllevel(recipe_type)
	
	chance=psvskill_level*10
	if recipe_group==99 or recipe_level==99 or recipe_type==99 then chance=0 end 
	
	if 	ZMiscThrowDice(chance)==1 then 
		item_id,item_count=ZGetRecipeGainItems(recipe_group,recipe_level,recipe_type)
		return item_id,item_count,402
	else
		return 0,0,403
	end 

end 
